import React from "react";
import Typography from "@material-ui/core/Typography";
import Input from "@material-ui/core/Input";
import Table from "@material-ui/core/Table";
import { TableHead, TableRow, TableCell } from "@material-ui/core";
import LocalPizzaSharpIcon from "@material-ui/icons/LocalPizzaSharp";
import AddCircleIcon from "@material-ui/icons/AddCircle";
import RemoveCircleSharpIcon from "@material-ui/icons/RemoveCircleSharp";
import PersonIcon from "@material-ui/icons/Person";
import AccessibilityNewIcon from "@material-ui/icons/AccessibilityNew";
import { connect } from "react-redux";
import { buyCake } from "../redux";

function CakeContainer(props) {
  return (
    <div>
      <div width="">
      <Table>
        <TableRow>
          <TableCell><LocalPizzaSharpIcon/></TableCell>
          <TableCell>Small</TableCell>
          <TableCell>
            <RemoveCircleSharpIcon  onClick={props.buyCake} style={{ color: "#000080" }}/>
          </TableCell>
          <TableCell>{props.numOfCakes}</TableCell>
          <TableCell><AddCircleIcon style={{ color: "#f10c45" }}/></TableCell>
        </TableRow>
      </Table>
      </div>
      <h2>Number of cakes - {props.numOfCakes} </h2>
      <button onClick={props.buyCake}>Buy Cake</button>
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    numOfCakes: state.cake.numOfCakes,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    buyCake: () => dispatch(buyCake()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(CakeContainer);
