                <Grid>
                  <Grid onClick={props.addAdult}>
                    <Grid
                      onClick={
                        props.numOfMediumPizzas === 1 &&
                        props.numOfSmallPizzas * 150 +
                          props.numOfMediumPizzas * 200 +
                          props.numOfLargePizzas * 300 <
                          1000
                          ? props.buyMediumPizza
                          : null
                      }
                    >
                      <AddCircleIcon
                        onClick={
                          props.numOfSmallPizzas * 150 +
                            props.numOfMediumPizzas * 200 +
                            props.numOfLargePizzas * 300 <
                          1000
                            ? props.numOfMediumPizzas == 1
                              ? props.addLargePizza
                              : props.addMediumPizza
                            : null
                        }
                        style={{
                          color:
                            props.numOfSmallPizzas * 150 +
                              props.numOfMediumPizzas * 200 +
                              props.numOfLargePizzas * 300 <
                            1000
