                    style={{
                      color: props.numOfLargePizzas === 0 ? "gray" : "#000080",
                      cursor: "pointer",
                    }}
                  />
                </Grid>
                <Grid>{props.numOfLargePizzas}</Grid>
                <Grid>
                  <AddCircleIcon
                    onClick={
                      props.numOfSmallPizzas * 150 +
                        props.numOfMediumPizzas * 200 +
                        props.numOfLargePizzas * 300 <
                      1000
                        ? props.addLargePizza
                        : null
                    }
                    style={{
                      color:
                        props.numOfSmallPizzas * 150 +
                          props.numOfMediumPizzas * 200 +
                          props.numOfLargePizzas * 300 <
                        1000
                          ? "#f10c45"
                          : "gray",
                      cursor: "pointer",
                    }}
                  />
                </Grid>
              </Grid>
            </Grid>
          </Grid>

          <hr></hr>
          <Grid
            item
            style={{
              display: "flex",
              justifyContent: "space-between",
              width: "100%",
