import React from "react";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import Input from "@material-ui/core/Input";
import Table from "@material-ui/core/Table";
import { TableHead, TableRow, TableCell } from "@material-ui/core";
import LocalPizzaSharpIcon from "@material-ui/icons/LocalPizzaSharp";
import AddCircleIcon from "@material-ui/icons/AddCircle";
import RemoveCircleSharpIcon from "@material-ui/icons/RemoveCircleSharp";
import PersonIcon from "@material-ui/icons/Person";
import AccessibilityNewIcon from "@material-ui/icons/AccessibilityNew";
import { connect } from "react-redux";
import { buyCake } from "./redux";
import { addCake } from "./redux";
import { buyMediumPizza } from "./redux";
import { addMediumPizza } from "./redux";
import { buyLargePizza } from "./redux";
import { addLargePizza } from "./redux";
import { decreaseAdult } from "./redux";
import { addAdult } from "./redux";
import { decreaseChildren } from "./redux";
import { addChildren } from "./redux";

const PizzaOrder = (props) => {
  return (
    <Grid>
      <div style={{ padding: "10%" }}>
        <Typography align="left" style={{ color: "#000080" }}>
          Order <b>Pizza</b>
        </Typography>
        <div style={{ align: "center", border: "2px solid #D3D3D3" }}>
          <Table style={{}}>
            <div width="50%" align="right">
              <TableRow>
                <TableCell  style={{borderBottom:"none"}}>
                  <LocalPizzaSharpIcon
                    style={{
                      fontSize: 20,
                      color: "#000080",
                      transform: "rotate(40deg)",
                    }}
                  />{" "}
                </TableCell>
                <TableCell>SMALL</TableCell>
                <TableCell>
                  <RemoveCircleSharpIcon
                    onClick={props.numOfCakes>0 ? (props.buyCake):(null) }
                    style={{ color: "gray" }}
                  />
                </TableCell>
                <TableCell>{props.numOfCakes}</TableCell>
                <TableCell>
                  <div onClick={props.addChildren}>
                  <AddCircleIcon
                    onClick={props.addCake}
                    style={{ color: "#f10c45" }}
                  />
                  </div>
                </TableCell>
              </TableRow>
              <TableRow>
                <TableCell>
                  <LocalPizzaSharpIcon
                    style={{
                      fontSize: 25,
                      color: "#000080",
                      transform: "rotate(40deg)",
                    }}
                  />{" "}
                </TableCell>
                <TableCell>MEDIUM</TableCell>
                <TableCell>
                  <RemoveCircleSharpIcon
                    onClick={props.numOfMediumPizzas>0? props.buyMediumPizza : null}
                    style={{ color: "#000080" }}
                  />
                </TableCell>
                <TableCell>{props.numOfMediumPizzas}</TableCell>
                <TableCell>
                  <div onClick={props.addAdult}>
                  <AddCircleIcon
                    onClick={props.addMediumPizza}
                    style={{ color: "#f10c45" }}
                  />
                </div>

                </TableCell>
              </TableRow>
              <TableRow>
                <TableCell>
                  <LocalPizzaSharpIcon
                    style={{
                      fontSize: 35,
                      color: "#000080",
                      transform: "rotate(40deg)",
                    }}
                  />{" "}
                </TableCell>
                <TableCell>LARGE</TableCell>
                <TableCell>
                  <RemoveCircleSharpIcon
                    onClick={props.numOfLargePizzas >0? props.buyLargePizza : null}
                    style={{ color: "#000080" }}
                  />
                </TableCell>
                <TableCell>{props.numOfLargePizzas}</TableCell>
                <TableCell>
                  <div onClick={++addAdult}>
                  <AddCircleIcon
                    onClick={props.addLargePizza}
                    style={{ color: "#f10c45" }}
                  />
                  </div>
                </TableCell>
              </TableRow>
            </div>
          </Table>

          <hr></hr>
          <div
            style={{
              display: "flex",
              justifyContent: "space-between",
              width: "100%",
            }}
          >
            <div style={{ display: "inline-flex" }}>
              <Table>
                <TableRow>
                  <TableCell>
                    {" "}
                    <PersonIcon style={{ color: "#000080" }} />
                  </TableCell>
                  <TableCell>
                    {" "}
                    <Typography>ADULTS</Typography>
                  </TableCell>
                </TableRow>
              </Table>
            </div>
            <div style={{ display: "inline-flex" }}>
              <Table>
                <TableRow>
                  <TableCell>
                    {" "}
                    <RemoveCircleSharpIcon

                      onClick=
                      {props.numOfAdults<=1? (null): (props.decreaseAdult)}
                      style={{ color: "#000080" }}
                    />
                  </TableCell>
                  <TableCell>
                    {" "}
                    {props.numOfAdults}
                  </TableCell>
                  <TableCell>
                    {" "}
                    <AddCircleIcon
                      onClick={props.addAdult}
                      style={{ color: "#f10c45" }}
                    />
                  </TableCell>
                </TableRow>
              </Table>
            </div>
          </div>
          <hr></hr>
          <div
            style={{
              display: "flex",
              justifyContent: "space-between",
              width: "100%",
            }}
          >
            <div style={{ display: "inline-flex" }}>
              <Table>
                <TableRow>
                  <TableCell>
                    {" "}
                    <PersonIcon style={{ color: "#000080" }} />
                  </TableCell>
                  <TableCell>
                    {" "}
                    <Typography>CHILDREN</Typography>
                  </TableCell>
                </TableRow>
              </Table>
            </div>
            <div style={{ display: "inline-flex" }}>
              <Table>
                <TableRow>
                  <TableCell>
                    {" "}
                    <RemoveCircleSharpIcon
                      onClick={props.decreaseChildren}
                      style={{ color: "#000080" }}
                    />
                  </TableCell>
                  <TableCell>
                    {" "}
                    {props.numOfChildren}
                  </TableCell>
                  <TableCell>
                    {" "}
                    <AddCircleIcon
                      onClick={props.addChildren}
                      style={{ color: "#f10c45" }}
                    />
                  </TableCell>
                </TableRow>
              </Table>
            </div>
          </div>
        </div>
        <div
          style={{ display: "flex",justifyContent:"space-between", color: "#000080", paddingTop: 20 }}
        >
          <div style={{ align: "left" }}>
            Order <b>Total</b>{" "}
          </div>{props.numOfCakes*150 + props.numOfMediumPizzas*200 + props.numOfLargePizzas*300}</div>
        {/* <div style={{align:"right", width:"88%", }}>
      <Typography>450</Typography>
      </div> */}
      </div>
    </Grid>
  );
};

const mapStateToProps = (state) => {
  return {
    numOfCakes: state.cake.numOfCakes,
    numOfMediumPizzas: state.mediumPizza.numOfMediumPizzas,
    numOfLargePizzas: state.largePizza.numOfLargePizzas,
    numOfAdults: state.adults.numOfAdults,
    numOfChildren: state.children.numOfChildren,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    buyCake: () => dispatch(buyCake()),
    addCake: () => dispatch(addCake()),
    buyMediumPizza: () => dispatch(buyMediumPizza()),
    addMediumPizza: () => dispatch(addMediumPizza()),
    buyLargePizza: () => dispatch(buyLargePizza()),
    addLargePizza: () => dispatch(addLargePizza()),
    decreaseAdult: () => dispatch(decreaseAdult()),
    addAdult: () => dispatch(addAdult()),
    decreaseChildren: () => dispatch(decreaseChildren()),
    addChildren: () => dispatch(addChildren()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(PizzaOrder);
