import React from "react";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import Input from "@material-ui/core/Input";
import Table from "@material-ui/core/Table";
import { TableHead, TableRow, TableCell } from "@material-ui/core";
import LocalPizzaSharpIcon from "@material-ui/icons/LocalPizzaSharp";
import AddCircleIcon from "@material-ui/icons/AddCircle";
import RemoveCircleSharpIcon from "@material-ui/icons/RemoveCircleSharp";
import PersonIcon from "@material-ui/icons/Person";
import AccessibilityNewIcon from "@material-ui/icons/AccessibilityNew";
import { connect } from "react-redux";
import { buyCake } from "./redux";
import { addCake } from "./redux";
import { buyMediumPizza } from "./redux";
import { addMediumPizza } from "./redux";
import { buyLargePizza } from "./redux";
import { addLargePizza } from "./redux";
import { decreaseAdult } from "./redux";
import { addAdult } from "./redux";
import { decreaseChildren } from "./redux";
import { addChildren } from "./redux";

const PizzaOrder = (props) => {
  return (
    <Grid>
    <div style={{ padding: "10%" }}>
      <Typography align="left" style={{ color: "#000080" }}>
        Order <b>Pizza</b>
      </Typography>
      <div style={{ align: "center", border: "2px solid #D3D3D3" }}>
        <Table>
          <div width="50%" align="right">
            <TableRow>
              <TableCell>
                <LocalPizzaSharpIcon
                  style={{
                    fontSize: 20,
                    color: "#000080",
                    transform: "rotate(30deg)",
                  }}
                />{" "}
              </TableCell>
              <TableCell>SMALL</TableCell>
              <TableCell>
                <RemoveCircleSharpIcon
                  onClick={props.buyCake}
                  style={{ color: "gray" }}
                />
              </TableCell>
              <TableCell>{props.numOfCakes}</TableCell>
              <TableCell>
                <AddCircleIcon
                  onClick={props.addCake}
                  style={{ color: "#f10c45" }}
                />
              </TableCell>
            </TableRow>
            <TableRow>
              <TableCell>
                <LocalPizzaSharpIcon
                  style={{
                    fontSize: 25,
                    color: "#000080",
                    transform: "rotate(30deg)",
                  }}
                />{" "}
              </TableCell>
              <TableCell>MEDIUM</TableCell>
              <TableCell>
                <RemoveCircleSharpIcon
                  onClick={props.buyMediumPizza}
                  style={{ color: "#000080" }}
                />
              </TableCell>
              <TableCell>{props.numOfMediumPizzas}</TableCell>
              <TableCell>
                <AddCircleIcon
                  onClick={props.addMediumPizza}
                  style={{ color: "#f10c45" }}
                />
              </TableCell>
            </TableRow>
            <TableRow>
              <TableCell>
                <LocalPizzaSharpIcon
                  style={{
                    fontSize: 35,
                    color: "#000080",
                    transform: "rotate(30deg)",
                  }}
                />{" "}
              </TableCell>
              <TableCell>LARGE</TableCell>
              <TableCell>
                <RemoveCircleSharpIcon
                  onClick={props.buyLargePizza}
                  style={{ color: "#000080" }}
                />
              </TableCell>
              <TableCell>{props.numOfLargePizzas}</TableCell>
              <TableCell>
                <AddCircleIcon
                  onClick={props.addLargePizza}
                  style={{ color: "#f10c45" }}
                />
              </TableCell>
            </TableRow>
          </div>
        </Table>

        <hr></hr>
        <div style={{ display:"flex",justifyContent:"space-between", width:"100%" }}>

          {/* <Table>
            <TableRow>
              <TableCell>
                <PersonIcon style={{ color: "#000080" }} />
              </TableCell>
              <TableCell> ADULTS</TableCell>
              <div style={{ paddingRight:0
              // , paddingLeft: "640px"
               }}>
                <TableCell>
                  <RemoveCircleSharpIcon onClick={props.decreaseAdult} style={{ color: "#000080" }} />
                </TableCell>{" "}
                <TableCell>{props.numOfAdults}</TableCell>{" "}
                <TableCell>
                  <AddCircleIcon onClick={props.addAdult} style={{ color: "#f10c45" }} />
                </TableCell>
              </div>
            </TableRow>
          </Table> */}
        </div>
        <hr></hr>
        <div style={{ align: "left", width: "50px" }}>
          <Table>
            <TableRow>
              <TableCell>
                <AccessibilityNewIcon style={{ color: "#000080" }} />
              </TableCell>
              <TableCell> CHILDREN</TableCell>
              <div style={{ align: "right"
              // , paddingLeft: "620px" 
              }}>
                <TableCell>
                  <RemoveCircleSharpIcon onClick={props.decreaseChildren} style={{ color: "#000080" }} />
                </TableCell>{" "}
                <TableCell>{props.numOfChildren}</TableCell>{" "}
                <TableCell>
                  <AddCircleIcon onClick={props.addChildren} style={{ color: "#f10c45" }} />
                </TableCell>
              </div>
            </TableRow>
          </Table>
        </div>
      </div>
      <div style={{ display: "inline-flex", color: "#000080", paddingTop: 20 }}>
        <div style={{align:"left"}}>
          Order <b>Total</b>{" "}
        </div>{" "}

        <div style={{ color: "black" }}>

        </div>
      </div>
      {/* <div style={{align:"right", width:"88%", }}>
      <Typography>450</Typography>
      </div> */}
    </div>
    </Grid>
  );
};

const mapStateToProps = (state) => {
  return {
    numOfCakes: state.cake.numOfCakes,
    numOfMediumPizzas: state.mediumPizza.numOfMediumPizzas,
    numOfLargePizzas: state.largePizza.numOfLargePizzas,
    numOfAdults: state.adults.numOfAdults,
    numOfChildren: state.children.numOfChildren
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    buyCake: () => dispatch(buyCake()),
    addCake: () => dispatch(addCake()),
    buyMediumPizza: () => dispatch(buyMediumPizza()),
    addMediumPizza: () => dispatch(addMediumPizza()),
    buyLargePizza: () => dispatch(buyLargePizza()),
    addLargePizza: () => dispatch(addLargePizza()),
    decreaseAdult: () => dispatch(decreaseAdult()),
    addAdult: () => dispatch(addAdult()),
    decreaseChildren: () => dispatch(decreaseChildren()),
    addChildren: () => dispatch(addChildren()),




  };
};

export default connect(mapStateToProps, mapDispatchToProps)(PizzaOrder);
